mapper.update:
update gate_machine




        where gate_machine_id = #{item.gateMachineId} then #{item.gateMachineName}






        where gate_machine_id = #{item.gateMachineId} then #{item.dutyPhone}






        where gate_machine_id = #{item.gateMachineId} then #{item.district}






        where gate_machine_id = #{item.gateMachineId} then #{item.gateMachineLongitude}






        where gate_machine_id = #{item.gateMachineId} then #{item.gateMachineLatitude}






        where gate_machine_id = #{item.gateMachineId} then #{item.address}






        where gate_machine_id = #{item.gateMachineId} then #{item.updateTime}




where gate_machine_id in

  #{item.gateMachineId}

mapper.delete:
delete from gate_machine
where gate_machine_id in

  #{item.gateMachineId}
